export const setProductsState = (products) => ({ 
    type:'GET_PRODUCTS',
    payload:products
})

export const setCartProducts = (products) => ({ 
    type:'SET_CART_PRODUCTS',
    payload:products
})
export const authUser = (user) => ({ 
    type:'AUTH_USER',
    payload:user
})

export const addVacationToRedux = (vacation) => ({ 
    type:'ADD_VACATION',
    payload:vacation
})

export const updateVacations = (vacations) => ({ 
    type:'UPDATE_VACATION',
    payload:vacations
})
