import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { Button } from '@material-ui/core'
import Modal from 'react-bootstrap/Modal'
// React toastify 
import { ToastContainer, toast, Zoom } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'

const DeleteVacationModal = ({ modalOpen, setModalOpen, orderId }) => {
    const [buttonDisabled, setButtonDisabled] = useState(false)
    const dispatch = useDispatch()
    const vacations = useSelector(state => state)

    const successToast = (message) => {
        toast(message, { 
         draggable: true, 
         position: toast.POSITION.BOTTOM_RIGHT,
         transition: Zoom,
         autoClose: 2000
        })
      }

    // Delete vacation
    const deleteVacation = async (id) => {
        try {
            setButtonDisabled(true)
            const res = await fetch('https://vacationweb.herokuapp.com/vacation',{
                method: 'DELETE',
                headers: {
                    'Content-Type':'application/json'
                },
                body: JSON.stringify({ id })
                })
            const { success } = await res.json()
            if(success) {
                const newVacationArray = vacations.filter(vacation => vacation.id !== id)
             
                setButtonDisabled(false)
                setModalOpen(false)
                successToast('Vacation Deleted! 🤓')
            }
        } catch (error) {
            console.log(error)
        }
    }
    
    return (
        <>
        <Modal size="md" centered show={modalOpen} onHide={() => setModalOpen(false)} style={{margin:'70px auto 30px', textAlign:'center'}}>
        <Modal.Header>
            <Modal.Title style={{fontWeight:'700'}}> Your order has been successfully placed! The order number is {orderId}</Modal.Title>
         </Modal.Header>
        <Modal.Body>
            <p>To download a receipt <Button> click here </Button></p>
            <Button disabled={buttonDisabled} className="my-3" variant="contained" color="secondary" type="submit" size="large" onClick={() => deleteVacation()}> Delete vacation </Button>
        </Modal.Body>
        </Modal>
        {/* ToastContainer */}
        <ToastContainer /> 
        </>
    )
}

export default DeleteVacationModal
