import React,{ useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom'
import Carusel from '../caursel/Carusel'
// import images
import SignUpPhoto from '../../images/signPhoto.png'
// material ui
import { makeStyles } from '@material-ui/core/styles'
import { Grid, TextField, Button, Link, Paper, Collapse, InputAdornment, Hidden, Stepper, Step, StepLabel } from '@material-ui/core'
import CloseIcon from '@material-ui/icons/Close'
import IconButton from '@material-ui/core/IconButton'
import Alert from '@material-ui/lab/Alert'
import VisibilityIcon from '@material-ui/icons/Visibility'
import VisibilityOffIcon from '@material-ui/icons/VisibilityOff'
// formik
import { Formik } from 'formik'
import * as Yup from 'yup'
// cloudinary
import { Image } from 'cloudinary-react'
// recaptcha
import ReCaptcha from 'react-google-recaptcha'
// css
import './style.css'

const useStyles = makeStyles((theme) => ({
    root: {
      marginTop:'20px'
    },
    image: {
      position:'relative',
      backgroundImage: `url(${SignUpPhoto})`,
      backgroundRepeat: 'no-repeat',
      alignSelf: 'center',
      backgroundPosition: '0 0',
      backgroundSize: '454px 618px',
      flexBasis: '454px',
      height: '618px',
    },
    slider: {
      width:'54%',
      margin: '99px 0 0 151px',
      position: 'relative',
      left:'-3px',
      bottom: '3px'
    },
    formGrid: {
      margin:'70px 15px'
    },
    paper: {
      margin: theme.spacing(3, 4),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    avatar: {
      margin: theme.spacing(1),
      backgroundColor: theme.palette.secondary.main,
    },
    form: {
      width: '100%',
      marginTop: theme.spacing(1),
    },
    profileBtn: {
      paddingTop:'10px',
      marginTop:'10px',
      marginBottom:'10px'
    },
    imageIcon: {
      marginBottom:'8px',
      marginRight:'5px'
    },
    nameTextField: {
      marginRight:'4px'
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
}))


const SignUp = ({ setIsAuth }) => {
  const [message, setMessage] = useState('')
  const [openAlert, setOpenAlert] = useState(false)
  const [showPassword, setShowPassword] = useState(false)
  const [showConfirmPassword, setShowConfirmPassword] = useState(false)
  const [buttonDisabled, setButtonDisabled] = useState(false)
  const [activeStep, setActiveStep] = useState(0)
  const steps = ['Basic authentication', 'Enter New Email', 'Are You Sure ?'];
  const classes = useStyles()
  const history = useHistory()
  
  // Form validation
  const validationSchema = Yup.object().shape({
    firstname:Yup.string()
    .required('first name is required!'),
    lastname:Yup.string()
    .required('last name is required!'),
    username:Yup.string()
    .required('user name is required!')
    .email(),
    password:Yup.string()
    .required('password is required!')
    .min(6, 'password have to be at least 6 characters long'),
    confirmPassword:Yup.string()
    .required('confirm your password!')
    .oneOf([Yup.ref('password'), null], 'Passwords must match')
    })

  const errorHelper = (formik,values) => ({
      error: formik.errors[values] && formik.touched[values] ? true : false,
      helperText: formik.errors[values] && formik.touched[values] ? formik.errors[values]:null
  })
 
  // Handle password visiblity 
  const  handleClickShowPassword = () => {
    setShowPassword(!showPassword)
  }

  // Handle confirm password visiblity 
  const handleClickConfirmPassword = () => {
    setShowConfirmPassword(!showConfirmPassword)
  }

  const handleNext = () => {
    setActiveStep((prev) => prev + 1)
}

const handleBack = () => {
    setActiveStep((prev) => prev - 1)
}
const nextBtn = () => (
    <Button className="m-3" variant="contained" color="primary" onClick={handleNext}>
        next
    </Button>
)

const prevBtn = () => (
    <Button className="m-3" variant="contained"  onClick={handleBack}>
        back
    </Button>
)

  // Add new user
  const signUpUser = async (values) => {
    try {
      setButtonDisabled(true)
         const response = await fetch('http://localhost:3001/user/signup', { 
         method: 'POST',
         headers: {
          'Content-Type':'application/json'
        },
        body: JSON.stringify({...values })
        })
        const data = await response.json()
  
        if(data.user){
          localStorage.setItem('user', JSON.stringify({...data.user, newUser:true }))
          setIsAuth(true)
          setButtonDisabled(false)
          history.push('/')
        } else {
          setMessage(data.error)
          setOpenAlert(true)
          setButtonDisabled(false)
        }
        } catch (error) {
            console.error(error)
          }
  }
  
    return (
      <Grid container className={classes.root}>
        <Hidden mdDown>
            <Grid item xs={1} md={2}></Grid>
            <Grid item xs={2} lg={4} className={classes.image} >
              <Carusel />
            </Grid>
      </Hidden>
 
      {/* Sign up form */}
      <Grid item xs={12} lg={4}  component={Paper} elevation={6} square className={classes.formGrid}>
          <div className={classes.paper}>
          <img  src="https://thumbs.dreamstime.com/b/vegetables-shopping-cart-trolley-grocery-logo-icon-design-vector-171090350.jpg" width="100" height="100" crop="scale" alt="cart" />
          <Stepper activeStep={activeStep}>
            {steps.map(step => (
                <Step key={step}> <StepLabel>{step}</StepLabel></Step>
            ))}
          </Stepper>
            <Formik
              initialValues={{username:'',password:'', firstname:'', lastname:''}}
              onSubmit={(values,{resetForm}) => signUpUser(values, resetForm)}
              validationSchema={validationSchema}
              enableReinitialize={true}>
              {(props) => (
                <>
                <form style={{textAlign:'center'}} onSubmit={props.handleSubmit} autoComplete="off">
                { activeStep === 0 && 
                <>
                <TextField name="username" margin="normal" label="User name" variant="outlined" fullWidth {...props.getFieldProps('username')} {...errorHelper(props,'username')}/>   
                 
               <TextField  type={showPassword ? "text": "password"} variant="outlined" margin="normal" fullWidth name="password" label="Password" {...props.getFieldProps('password')} {...errorHelper(props,'password')} 
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton onClick={handleClickShowPassword}> {showPassword ? <VisibilityOffIcon /> : <VisibilityIcon />}  </IconButton>
                      </InputAdornment>
                  )}} />

                <TextField type={showConfirmPassword ? "text": "password"} variant="outlined" margin="normal" fullWidth name="confirmPassword" label="Confirm Password" {...props.getFieldProps('confirmPassword')} {...errorHelper(props,'confirmPassword')} 
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton onClick={handleClickConfirmPassword}> {showConfirmPassword ? <VisibilityOffIcon /> : <VisibilityIcon />}  </IconButton>
                      </InputAdornment>
                  )}} />

                {props.values.username && !props.errors.username
                && props.values.password && !props.errors.password
                && props.values.confirmPassword && !props.errors.confirmPassword ?
                <Button disabled={false} className="my-3" variant="contained" color="primary" size="large" fullWidth onClick={handleNext}> Next </Button>
                :
                <Button disabled={true} className="my-3" variant="contained" color="primary" size="large" fullWidth> Next </Button>
                }
                </>
                }
                                        
                      
                  {/* <div style={{display:'flex'}}>
                  <TextField className={classes.nameTextField} name="firstname" margin="normal" label="First name" variant="outlined" fullWidth {...props.getFieldProps('firstname')} {...errorHelper(props,'firstname')}/>
                    <TextField name="lastname" margin="normal" label="Last name" variant="outlined" fullWidth {...props.getFieldProps('lastname')} {...errorHelper(props,'lastname')}/>   
                  </div> */}
              
               
                
                                      

                  {/* Alert error */}
                  <Collapse in={openAlert}>
                    <Alert
                      severity="error"
                      action={ <IconButton color="inherit" size="small" onClick={() => setOpenAlert(false) }> <CloseIcon fontSize="inherit" /> </IconButton>}>
                      {message}
                    </Alert>
                  </Collapse>

                 
                  <Grid container>
                    <Grid item>
