import React, { useState, useEffect } from 'react';
import clsx from 'clsx';
import './style.css'
// Components
import Search from '../search/Search'
import { Link } from 'react-router-dom'
import { makeStyles, useTheme } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton'
import BarChartIcon from '@material-ui/icons/BarChart'
import ExitToAppIcon from '@material-ui/icons/ExitToApp'
import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';

import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import { Nav, Navbar } from 'react-bootstrap'

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    backgroundColor: 'white',
    color:'black'
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  title: {
      color:'black'
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
}));

const SideDrawer = () => {
  const cartItems = JSON.parse(localStorage.getItem('availableCart')).CartItems
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = useState(false)
  const [cartProducts, setCartProducts] = useState([])

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const getProducts = () => {
    const itemsArr = []
    cartItems.forEach(item => {
      const cartItem = {
        id: item.ProductId,
        name: item.Product.name,
        singlePrice: item.Product.price,
        totalPrice: item.price,
        amount: item.amount,
        image: item.Product.image
      }
      itemsArr.push(cartItem)
    })
    setCartProducts(itemsArr)
  }

  useEffect(() => {
    getProducts()
  }, [])

  return (
    <div className={classes.root}>
      {/* Top navbar */}
      <AppBar position="fixed" className={clsx(classes.appBar, {[classes.appBarShift]: open})}>
        <Toolbar>
          <IconButton color="inherit" onClick={handleDrawerOpen} edge="start" className={clsx(classes.menuButton, open && classes.hide)}>
            <ShoppingCartIcon />
          </IconButton>
          <Navbar bg="white" expand="md" className="navbar">
              <Navbar.Brand style={{height:'40px'}}>
                <Link to="/" style={{ textDecoration: 'none', marginRight:'25%'}}>
                  <Typography className={classes.title} variant="h6" noWrap> ShoppingApp </Typography>
                </Link>
              </Navbar.Brand>
              <Navbar.Collapse className="navbar_collapse">
                <Nav className="mr-auto">
                        <Nav.Link> 
                            <Link to="/1" style={{ textDecoration: 'none', color:'black', marginTop:'7px' }}>
                            <IconButton color="inherit">
                            <span className="link_desc">Dairy and eggs</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <Nav.Link> 
                            <Link to="/2" style={{ textDecoration: 'none', color:'black', marginTop:'7px' }}>
                            <IconButton color="inherit">
                            <span className="link_desc">Meat</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <Nav.Link> 
                            <Link to="/3" style={{ textDecoration: 'none', color:'black', marginTop:'7px'}}>
                            <IconButton color="inherit">
                            <span className="link_desc">Fruits And Vegetables</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <Nav.Link> 
                            <Link to="/4" style={{ textDecoration: 'none', color:'black', marginTop:'7px'}}>
                            <IconButton color="inherit">
                            <span className="link_desc">Drinks and alcohol</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <Nav.Link> 
                            <Link to="/4" style={{ textDecoration: 'none', color:'black', marginTop:'7px'}}>
                            <IconButton color="inherit">
                            <span className="link_desc">Baking and canning cooking</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <div className={classes.searchLink}> <Search/> </div>
                        <Nav.Link> 
                          <Link to="/logout" style={{ textDecoration: 'none', color:'black', marginTop:'7px' }}>
                              <IconButton color="inherit">
                              <ExitToAppIcon/> <span className="link_desc">Logout</span>
                              </IconButton>
                          </Link>
                        </Nav.Link>
                    </Nav>
              </Navbar.Collapse>
          </Navbar>
        </Toolbar>
      </AppBar>

      {/* Side bar */}
      <Drawer className={classes.drawer} variant="persistent" anchor="left" open={open}  classes={{ paper: classes.drawerPaper }}>
        <div className={classes.drawerHeader}>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </div>
        <Divider />
        <div class="summary_card">
          {cartProducts.map(product => (
      
      <div class="card_item">
        <div class="product_img">
          <img src={product.image} alt="" />
        </div>
        <div class="product_info">
          <h1>{product.name}</h1>
        
        
          <div class="product_rate_info">
            <h1>{product.totalPrice}$</h1>
            <span class="pqt-minus">-</span>
            <span class="pqt">{product.amount}</span>
            <span class="pqt-plus">+</span>
          </div>
        </div>
      </div>
   
          ))}
          </div>
     
        <Divider />
      
      </Drawer>
      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      >
        <div className={classes.drawerHeader} />
       
      </main>
    </div>
  );
}

export default SideDrawer
