export default function productsReducer(state={data:[], cartProducts: [], totalPrice:0},action) {
    switch(action.type){
        case 'GET_PRODUCTS':
            return {...state,data:[...action.payload]}
        case 'SET_CART_PRODUCTS':
            return {...state, cartProducts:[...action.payload]}
        case 'ADD_CART_PRODUCTS':
            return {...state, cartProducts:[...state.cartProducts, action.payload]}
        case 'UPDATE_TOTAL_PRICE':
            return {...state,totalPrice: state.totalPrice + action.payload}
        case 'RESERT_TOTAL_PRICE':
            return {...state,totalPrice: 0}
        default:
            return state;
    }
}
