import React from 'react'
// Material ui
import { Divider, IconButton, Card, CardContent, CardHeader,
CardMedia, Avatar, Typography, CardActionArea, CardActions, Button } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'  
import DeleteIcon from '@material-ui/icons/Delete'
import EditIcon from '@material-ui/icons/Edit'
// Cloudinary
import { Image } from 'cloudinary-react'
import './style.css'

const useStyles = makeStyles(() => ({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },

  }))     
  
const VacationCard = ({ product, editVacation, deleteVacation, setFollow, isAdmin }) => {
    const classes = useStyles()
    return (
      <>
     { product.image &&
      <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={product.image}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
          {product.image}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
            across all continents except Antarctica
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary">
          Share
        </Button>
        <Button size="small" color="primary">
          Learn More
        </Button>
      </CardActions>
    </Card>
        }
    
    </>
    )
}

export default VacationCard
