import React,{ useState, useEffect } from 'react'
import { useHistory, Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { authUser } from '../../store/actions'
import SignInPhoto from '../../images/signPhoto.png'
// material ui
import { Grid, TextField, Button, Paper, Collapse, IconButton, InputAdornment, ListItemAvatar,
Avatar, ListItem, List, ListItemText } from '@material-ui/core'
import VpnKeyIcon from '@material-ui/icons/VpnKey'
import KitchenIcon from '@material-ui/icons/Kitchen'
import InsertInvitationIcon from '@material-ui/icons/InsertInvitation'
import CloseIcon from '@material-ui/icons/Close'
import VisibilityIcon from '@material-ui/icons/Visibility'
import VisibilityOffIcon from '@material-ui/icons/VisibilityOff'
import MoneyOffIcon from '@material-ui/icons/MoneyOff'
import PaymentIcon from '@material-ui/icons/Payment'
import HomeWorkIcon from '@material-ui/icons/HomeWork'
import LocalShippingIcon from '@material-ui/icons/LocalShipping';
import { makeStyles } from '@material-ui/core/styles'
import Alert from '@material-ui/lab/Alert'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import SearchBar from "material-ui-search-bar";

// formik
import { useFormik } from 'formik'
import * as Yup from 'yup'

// cloudinary
import { Image } from 'cloudinary-react'

  
const useStyles = makeStyles((theme) => ({
    root: {
     padding:'0px'
    },
    image: {
      position:'relative',
      backgroundImage: `url(${SignInPhoto})`,
      backgroundRepeat: 'no-repeat',
      alignSelf: 'center',
      backgroundPosition: '0 0',
      backgroundSize: '454px 618px',
      flexBasis: '454px',
      height: '618px',
    },
    formGrid: {
      margin:'70px 15px '
    },
    paper: {
      margin: theme.spacing(8, 4),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    form: {
      width: '100%',
      marginTop: theme.spacing(1),
    },
 
}))



const Payment = () => {
    const [message, setMessage] = useState('')
    const [openAlert, setOpenAlert] = useState(false)
    const [showPassword, setShowPassword] = useState(false)
    const [buttonDisabled, setButtonDisabled] = useState(false)
    const [numberOfProducts, setnumberOfProducts] = useState(null)
    const [numberOfOrders, setNumberOfOrders] = useState(null)
    const [availableCart, setAvailableCart] = useState(null)
    const [availableCartSum, setAvailableCartSum] = useState(null)
    const [userLastOrder, setUserLastOrder] = useState(null)
    const [newUser, setNewUser] = useState(null)
    const [productsImages, setProductsImages] = useState([])
    const [searched, setSearched] = useState("")
    const [rows, setRows] = useState([])
    const [dense, setDense] = useState(false);
    const history = useHistory()
    const dispatch = useDispatch()
    const classes = useStyles()
    const cartItems = useSelector(state => state.products.cartProducts)
    const totalPrice = useSelector(state => state.products.totalPrice)

    // Formik
    const formik = useFormik({
      initialValues:{username:'',password:''},
      validationSchema:Yup.object({
          username:Yup.string()
          .required('sorry username is required'),
          password:Yup.string()
          .required('password is required')
          .min(6, 'password have to be at least 6 characters long')
      }),
      onSubmit:(values,{resetForm}) => {                               
        order(values)
      }
    })

    const errorHelper = (formik,values) => ({
        error: formik.errors[values] && formik.touched[values] ? true : false,
        helperText: formik.errors[values] && formik.touched[values] ? formik.errors[values]:null
    })

    const order = () => {

    }
 
    const getProducts =   () => {
      const itemsArr = []
      cartItems.forEach(item => {
        itemsArr.push(item)
      })
      setRows(itemsArr)
    }
    const requestSearch = (searchedVal) => {
      const filteredRows = rows.filter((row) => row.name.toLowerCase().includes(searchedVal.toLowerCase()))
      setRows(filteredRows)
  }
  
  const cancelSearch = () => {
    setSearched("");
    requestSearch(searched);
  };

    useEffect(() => {
    
      getProducts()
    }, [])
  

  return (
   
    <Grid container className={classes.root}>

<Grid item xs={12} sm={5}  component={Paper} square className={classes.formGrid}>
<TableContainer component={Paper}>
<SearchBar
    value={searched}
    onChange={(searchVal) => requestSearch(searchVal)}
    onCancelSearch={() => cancelSearch()}
  />
      <Table className={classes.table} aria-label="simple table">
  
        <TableHead>
          <TableRow>
            <TableCell>Product</TableCell>
            <TableCell>quantity</TableCell>
            <TableCell>total price</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.id}>
              <TableCell>
              <Image className="product_img"  cloudName="malachcloud" src={row.image} width="70" height="40" crop="scale" />
                {row.name}
              </TableCell>
              <TableCell>{row.amount}</TableCell>
              <TableCell>{row.totalPrice}$</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
    
    </Grid>
   
        <Grid item xs={12} sm={5} component={Paper}  square className={classes.formGrid}>
        <div className={classes.paper}>
        <img src="https://thumbs.dreamstime.com/b/vegetables-shopping-cart-trolley-grocery-logo-icon-design-vector-171090350.jpg" width="100" height="100" crop="scale" alt="cart" />
          <form className={classes.form} noValidate onSubmit={formik.handleSubmit}>
            <TextField  variant="outlined" margin="normal" fullWidth label="user name" name="email" autoFocus {...formik.getFieldProps('username')} {...errorHelper(formik,'username')}/>
                {/* Error Alert */}
                <Collapse in={openAlert}>
                  <Alert
                    severity="error"
                    action={ <IconButton color="inherit" size="small" onClick={() => setOpenAlert(false) }> <CloseIcon fontSize="inherit" /> </IconButton>}>
                    {message}
                  </Alert>
                </Collapse>
                <Button disabled={buttonDisabled} className="my-3" variant="contained" color="primary" type="submit" fullWidth> Sign In </Button>
                <Grid container>
                  <Grid item>
                    <Link to="/signup" variant="body2"> Don't have an account? Sign Up </Link>
                  </Grid>
                </Grid>
            </form>
          </div>
        </Grid>
 
      

   
      </Grid>
  
    )
}
export default Payment
