import React, { useEffect } from 'react'
// Redux
import { useDispatch, useSelector } from 'react-redux'
import { setProductToUpdate, setCartProducts, addCartProducts, updateTotalPrice, setDrawerState } from '../../store/actions'
// Material ui
import { Divider, IconButton, Card, CardContent, CardHeader,
  Button, CardMedia, Avatar, Typography } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'  
import DeleteIcon from '@material-ui/icons/Delete'
import EditIcon from '@material-ui/icons/Edit'
import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
// React toastify 
import { ToastContainer, toast, Zoom } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
// Cloudinary
import { Image } from 'cloudinary-react'
import './style.css'

const useStyles = makeStyles(() => ({
    root: {
      display:'flex',
      justifyContent:'center',
      margin:'30px'
    },
    media: {
      height: '250px',
      width:'250px',
    },
    previewmedia: {
      width:'300px',
      height:'200px'
    },
    fab: {
      position:'fixed',
      bottom: '10px',
      left: '75%',
      whiteSpace: 'nowrap',
      zIndex: '50px',
      borderRadius:'20px',
      backgroundColor:'lightblue'
    },
    follow:{
      textAlign:'right'
    },
    card: {
      backgroundImage:"fff",
  },
    title: { 
      fontWeight:'600',
      fontSize:'16px'
    },
    divider: {
      margin:'15px 0px'
    },
    icon: {
      cursor:'pointer',
      margin:'0px 5px',
       "&:hover": {
        backgroundColor: '#F5F3F3',
        borderRadius:'100px',
      }
    }
  }))     
  
const ProductCard = ({ product, reduceQuantity, addQuantity }) => {
    const dispatch = useDispatch()
    const cart = JSON.parse(localStorage.getItem('availableCart'))
    const products = useSelector(state => state.products.data)
    const cartProducts = useSelector(state => state.products.cartProducts)
    const isAdmin = useSelector(state => state.users.isAdmin)
    const classes = useStyles()

       // Toastify
       const successToast = (message) => {
        toast(message, { 
         draggable: true, 
         position: toast.POSITION.BOTTOM_RIGHT,
         transition: Zoom,
         autoClose: 2000
        })
      }


    const addToCart = async (id, amount, price) => {
      const response = await fetch('/cart_item', { 
        method: 'POST',
        headers: {
         'Content-Type':'application/json'
       },
       body: JSON.stringify({ amount, price: price * amount, ProductId: id, CartId: cart.id })
       })
       const { cartItem } = await response.json() 
       console.log(cartItem)
       if(cartItem) {
        const cartItemsArr = cartProducts
        const changedItem = cartItemsArr.find(item => item.id === cartItem.ProductId)
        if(changedItem) {
          dispatch(updateTotalPrice( cartItem.price - changedItem.totalPrice))
          // changedItem.amount = cartItem.amount
          // changedItem.totalPrice = cartItem.price
          console.log(cartItemsArr);
          dispatch(setCartProducts(cartItemsArr))
        } else {
         const newProductInCart = { 
           id: cartItem.ProductId,
           name: cartItem.Product.name,
           singlePrice: cartItem.Product.price,
           amount: cartItem.amount,
           totalPrice:cartItem.price,
           image: cartItem.Product.image
         }
          dispatch(addCartProducts(newProductInCart))
          dispatch(updateTotalPrice(newProductInCart.totalPrice))
          successToast('Product added to cart! ðŸ˜€')
     
        }
       }
    }

    const updateProduct = (id) => {
      const productToUpdate = products.find(product => product.id === id)
      dispatch(setProductToUpdate(productToUpdate))
      dispatch(setDrawerState(true))
    }

 
    return (
      <>
     { product.image &&
   
      <div class="contain">
      <Image cloudName="malachcloud" src={product.image} width="250" height="200" crop="scale" />
  <h3>{product.name}</h3>
 
 
  <div class="properties">
  {!isAdmin &&
    <div class="qty">
      <h4>Quantity</h4>
       <RemoveIcon className={classes.icon} fontSize="small" color="action"   onClick={() => reduceQuantity(product.id)}/> 
      <span class="number">{product.quantity}</span>
      <AddIcon className={classes.icon} fontSize="small" color="action"  onClick={() => addQuantity(product.id)}/>
    </div>
  }
    <div class="price">
      <h4>Price</h4>
      <span class="price-inr">
        <i class="fa fa-inr"></i>
        <span class="amount">${product.price}</span>
      </span>
    </div>
    {!isAdmin &&
    <div class="price">
      <h4>Total</h4>
      <span class="price-inr">
        <i class="fa fa-inr"></i>
        <span class="amount">${+product.price * +product.quantity}</span>
      </span>
    </div>
    }
  </div>
    
 {!isAdmin && <input class="ip-add-cart" type="button" value={product.isInCart ? 'Update' : 'Add to cart'} onClick={() => addToCart(product.id, product.quantity, product.price )} /> }
 {isAdmin && <input class="ip-add-cart" type="button" value="Update product" onClick={() => updateProduct(product.id)} /> }
</div>



     }	
 </>


        
    
   
    )
}

export default ProductCard
