import React, { useState, useEffect } from 'react';
// Redux
import { useDispatch, useSelector } from 'react-redux'
import { setCartProducts } from '../../store/actions'
import clsx from 'clsx';
import './style.css'
// Cloudinary
import { Image } from 'cloudinary-react'
// Components
import Search from '../search/Search'
import { Link } from 'react-router-dom'
import { makeStyles, useTheme } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton'
import BarChartIcon from '@material-ui/icons/BarChart'
import ExitToAppIcon from '@material-ui/icons/ExitToApp'
import Drawer from '@material-ui/core/Drawer';
import CloseIcon from '@material-ui/icons/Close';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Button from '@material-ui/core/Button'
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import { Nav, Navbar } from 'react-bootstrap'

const drawerWidth = 300;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    backgroundColor: 'white',
    color:'black'
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  title: {
      color:'black'
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
}));

const SideDrawer = () => {
  const cart = JSON.parse(localStorage.getItem('availableCart'))
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = useState(false)
  const cartProducts = useSelector(state => state.products.cartProducts)
  const dispatch = useDispatch()
  const [totalPrice, setTotalPrice] = useState(0)

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  }

  const clearCart = async () => {
    const res = await fetch('/cart', {
      method: 'DELETE',
      headers: {
        'Content-Type':'application/json'
      },
      body: JSON.stringify({ id: cart.id })
      })
    const { success } = await res.json()
    if(success) {
     dispatch(setCartProducts([]))
   }
  }

  const removeFromCart = async (id) => {
    const res = await fetch('/cart_item', {
      method: 'DELETE',
      headers: {
        'Content-Type':'application/json'
      },
      body: JSON.stringify({ CartId: cart.id, ProductId: id })
      })
    const { success } = await res.json()
    if(success) {
     const cartProductsArr = cartProducts.filter(item => item.id !== id)
     dispatch(setCartProducts(cartProductsArr))
   }
  }

  const getProducts =  async () => {
    const res = await fetch(`/cart/${cart.id}`)
    const data  = await res.json()
    const itemsArr = []
    let count = 0
    data.cart.CartItems.forEach(item => {
      const cartItem = {
        id: item.ProductId,
        name: item.Product.name,
        singlePrice: item.Product.price,
        totalPrice: item.price,
        amount: item.amount,
        image: item.Product.image
      }
      count += item.price
      itemsArr.push(cartItem)
    })
    setTotalPrice(count)
    dispatch(setCartProducts(itemsArr))
    
  }

  useEffect(() => {
    getProducts()
  }, [])

  return (
    <div className={classes.root}>
      {/* Top navbar */}
      <AppBar position="fixed" className={clsx(classes.appBar, {[classes.appBarShift]: open})}>
        <Toolbar>
          <IconButton color="inherit" onClick={handleDrawerOpen} edge="start" className={clsx(classes.menuButton, open && classes.hide)}>
            <ShoppingCartIcon />
          </IconButton>
          <Navbar bg="white" expand="md" className="navbar">
              <Navbar.Brand style={{height:'40px'}}>
                <Link to="/" style={{ textDecoration: 'none', marginRight:'25%'}}>
                  <Typography className={classes.title} variant="h6" noWrap> ShoppingApp </Typography>
                </Link>
              </Navbar.Brand>
              <Navbar.Collapse className="navbar_collapse">
                <Nav className="mr-auto">
                        <Nav.Link> 
                            <Link to="/1" style={{ textDecoration: 'none', color:'black', marginTop:'7px' }}>
                            <IconButton color="inherit">
                            <span className="link_desc">Dairy and eggs</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <Nav.Link> 
                            <Link to="/2" style={{ textDecoration: 'none', color:'black', marginTop:'7px' }}>
                            <IconButton color="inherit">
                            <span className="link_desc">Meat</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <Nav.Link> 
                            <Link to="/3" style={{ textDecoration: 'none', color:'black', marginTop:'7px'}}>
                            <IconButton color="inherit">
                            <span className="link_desc">Fruits And Vegetables</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <Nav.Link> 
                            <Link to="/4" style={{ textDecoration: 'none', color:'black', marginTop:'7px'}}>
                            <IconButton color="inherit">
                            <span className="link_desc">Drinks and alcohol</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <Nav.Link> 
                            <Link to="/4" style={{ textDecoration: 'none', color:'black', marginTop:'7px'}}>
                            <IconButton color="inherit">
                            <span className="link_desc">Baking and canning cooking</span>
                            </IconButton>
                            </Link>
                        </Nav.Link>
                        <div className={classes.searchLink}> <Search/> </div>
                        <Nav.Link> 
                          <Link to="/logout" style={{ textDecoration: 'none', color:'black', marginTop:'7px' }}>
                              <IconButton color="inherit">
                              <ExitToAppIcon/> <span className="link_desc">Logout</span>
                              </IconButton>
                          </Link>
                        </Nav.Link>
                    </Nav>
              </Navbar.Collapse>
          </Navbar>
        </Toolbar>
      </AppBar>

      {/* Side bar */}
      <Drawer className={classes.drawer} variant="persistent" anchor="left" open={open}  classes={{ paper: classes.drawerPaper }}>
        <div className={classes.drawerHeader}>
        <h3>Your Cart</h3>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <div class="summary_card">
          {cartProducts.map(product => (
      
      <div class="card_item">
        <div>
        <Image class="product_img"  cloudName="malachcloud" src={product.image} width="70" height="40" crop="scale" />
        </div>
        <div class="product_info">
          <h6>{product.name}</h6>
          <h6>quanity: {product.amount}</h6>
          <h6>total price: {product.totalPrice}$</h6>
        </div>
        <div>
         <IconButton onClick={() => removeFromCart(product.id)}> <CloseIcon/> </IconButton> 
        </div>
      </div>
   
          ))}
          </div>
          <h5>Total price: {totalPrice}</h5>
          <div class="btn-group">
            <Button variant="contained" color="primary" className="m-1"> Payment page </Button>
            <Button variant="contained" color="secondary"  className="m-1" onClick={clearCart}> Clear cart </Button>
          </div>
          

      
      </Drawer>
      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      >
        <div className={classes.drawerHeader} />
       
      </main>
    </div>
  );
}

export default SideDrawer
