import React, { useState, useEffect } from 'react'
import { useLocation } from 'react-router-dom'
// Redux
import { useDispatch, useSelector } from 'react-redux'
import { setCartProducts, updateTotalPrice, resetTotalPrice } from '../../store/actions'
import clsx from 'clsx';
import './style.css'
// formik
import { Formik } from 'formik'
import * as Yup from 'yup'
// Cloudinary
import { Image } from 'cloudinary-react'
// Components
import Search from '../search/Search'
import { Link } from 'react-router-dom'
import { Grid, TextField, Button, Collapse, Divider  } from '@material-ui/core'

import { makeStyles, useTheme } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton'
import BarChartIcon from '@material-ui/icons/BarChart'
import ExitToAppIcon from '@material-ui/icons/ExitToApp'
import Drawer from '@material-ui/core/Drawer';
import CloseIcon from '@material-ui/icons/Close';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';

import { Nav, Navbar } from 'react-bootstrap'

const drawerWidth = 300;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    fontFamily:'roboto'
  },
  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    backgroundColor: 'white',
    color:'black'
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  title: {
      color:'black'
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    justifyContent: 'space-around',
    marginTop:'10px'
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
}));

const SideBarCart = (props) => {
  const cart = JSON.parse(localStorage.getItem('availableCart'))
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = useState(false)
  const [isDelete, setIsDelete] = useState(true)
  const [initialValues, setInitialValues] = useState({})
  const cartProducts = useSelector(state => state.products.cartProducts)
  const totalPrice = useSelector(state => state.products.totalPrice)
  const isAdmin = useSelector(state => state.users.isAdmin)
  const productToUpdate = useSelector(state => state.products.productToUpdate)
  const dispatch = useDispatch()
  const location = useLocation()
 
  
  // Form validation
  const validationSchema = Yup.object().shape({
    name:Yup.string()
    .required('name is required!'),
    price:Yup.string()
    .required('price is required!'),
    image:Yup.string()
    .required('image is required!'),
    category:Yup.string()
    .required('category is required!')
    })

  const errorHelper = (formik,values) => ({
      error: formik.errors[values] && formik.touched[values] ? true : false,
      helperText: formik.errors[values] && formik.touched[values] ? formik.errors[values]:null
  })
  const handleDrawerOpen = () => {
    setOpen(true);
  }

  const handleDrawerClose = () => {
    setOpen(false);
  }

  const clearCart = async () => {
    const res = await fetch('/cart_item/cart', {
      method: 'DELETE',
      headers: {
        'Content-Type':'application/json'
      },
      body: JSON.stringify({ id: cart.id })
      })
    const { success } = await res.json()
    if(success) {
     dispatch(setCartProducts([]))
     dispatch(resetTotalPrice())

   }
  }

  const removeFromCart = async (id) => {
    const res = await fetch('/cart_item', {
      method: 'DELETE',
      headers: {
        'Content-Type':'application/json'
      },
      body: JSON.stringify({ CartId: cart.id, ProductId: id })
      })
    const { success } = await res.json()
    if(success) {
     const deletedItem = cartProducts.find(item => item.id === id)
     console.log(deletedItem);
     const cartProductsArr = cartProducts.filter(item => item.id !== id)
     dispatch(setCartProducts(cartProductsArr))
     dispatch(updateTotalPrice(-deletedItem.totalPrice))
   }
  }

  const updateProduct = () => {
    console.log('a');
  }

  const getProducts =  async () => {
    const res = await fetch(`/cart/${cart.id}`)
    const data  = await res.json()
    console.log(data);
    const itemsArr = []
    let count = 0
    data.cart.CartItems.forEach(item => {
      const cartItem = {
        id: item.ProductId,
        name: item.Product.name,
        singlePrice: item.Product.price,
        totalPrice: item.price,
        amount: item.amount,
        image: item.Product.image
      }
      count += item.price
      itemsArr.push(cartItem)
    })
    if(count !== totalPrice) {
      dispatch(updateTotalPrice(count))
    }
    dispatch(setCartProducts(itemsArr))   
  }

  useEffect(() => {
    getProducts()
  }, [])

  useEffect(() => {
    setInitialValues({...productToUpdate})
  }, [productToUpdate])

  // disable the delete product option on the payment page
  useEffect(() => {
  location.pathname === '/payment' ? setIsDelete(false) : setIsDelete(true)
  }, [location.pathname])

  return (
    <div className={classes.root}>
      {/* Top navbar */}
      <AppBar position="fixed" className={clsx(classes.appBar, {[classes.appBarShift]: open})}>
        <Toolbar>
          <IconButton color="inherit" onClick={handleDrawerOpen} edge="start" className={clsx(classes.menuButton, open && classes.hide)}>
            <ShoppingCartIcon />
          </IconButton>
          <Navbar bg="white" expand="md" className="navbar">
              <Navbar.Brand style={{height:'40px'}}>
                <Link to="/" style={{ textDecoration: 'none', marginRight:'25%'}}>
                  <Typography className={classes.title} variant="h6" noWrap> ShoppingApp </Typography>
                </Link>
              </Navbar.Brand>
              <Navbar.Collapse className="navbar_collapse">
                <Nav className="mr-auto">
                 
                            <Link to="/1" style={{ textDecoration: 'none', color:'black', marginTop:'7px' }}>
                            <IconButton color="inherit">
                            <span className="link_desc">Dairy and eggs</span>
                            </IconButton>
                            </Link>
                       
                            <Link to="/2" style={{ textDecoration: 'none', color:'black', marginTop:'7px' }}>
                            <IconButton color="inherit">
                            <span className="link_desc">Meat</span>
                            </IconButton>
                            </Link>
                 
                            <Link to="/3" style={{ textDecoration: 'none', color:'black', marginTop:'7px'}}>
                            <IconButton color="inherit">
                            <span className="link_desc">Fruits And Vegetables</span>
                            </IconButton>
                            </Link>
                      
                            <Link to="/4" style={{ textDecoration: 'none', color:'black', marginTop:'7px'}}>
                            <IconButton color="inherit">
                            <span className="link_desc">Drinks and alcohol</span>
                            </IconButton>
                            </Link>
                        
                
                            <Link to="/4" style={{ textDecoration: 'none', color:'black', marginTop:'7px'}}>
                            <IconButton color="inherit">
                            <span className="link_desc">Baking and canning cooking</span>
                            </IconButton>
                            </Link>
                       
                        <div className={classes.searchLink}> <Search/> </div>
                   
                          <Link to="/logout" style={{ textDecoration: 'none', color:'black', marginTop:'7px' }}>
                              <IconButton color="inherit">
                              <ExitToAppIcon/> <span className="link_desc">Logout</span>
                              </IconButton>
                          </Link>
                      
                    </Nav>
              </Navbar.Collapse>
          </Navbar>
        </Toolbar>
      </AppBar>

      {/* Side bar */}
  
      <Drawer className={classes.drawer} variant="persistent" anchor="left" open={open}  classes={{ paper: classes.drawerPaper }}>
        {!isAdmin ? 
        <>
        <div className={classes.drawerHeader}>
          <h3 className="cart_header">Your Cart</h3>
          <div>
            <IconButton onClick={handleDrawerClose}>
              <ChevronLeftIcon/>
            </IconButton>
          </div>
        </div>
        <Divider />
        <div className="summary_card">
          {cartProducts.map(product => (
            <div className="card_item" key={product.id}>
              <div>
                <Image className="product_img"  cloudName="malachcloud" src={product.image} width="70" height="40" crop="scale" />
              </div>
              <div className="product_info">
                <h6>{product.name}</h6>
                <h6>quanity: {product.amount}</h6>
                <h6>total price: {product.totalPrice}$</h6>
              </div>
              <div>
                { isDelete && <IconButton onClick={() => removeFromCart(product.id)}> <CloseIcon/> </IconButton> }
              </div>
            </div>
            ))}
          </div>

          {totalPrice === 0 ?
            <h5 className="m-2">Your cart is empty</h5>
           :
            <h5 className="ml-2"><span className="total_price_span"> Total price: </span> ${totalPrice}</h5>
          }
    
          <div className="btn-group">
          <Link to="/payment" style={{ textDecoration: 'none', pointerEvents: totalPrice === 0 || !isDelete ? 'none' : ''}}>
            <Button variant="outlined" color="primary" className="m-2" disabled={totalPrice === 0 || !isDelete}> Payment page </Button>
          </Link> 
            <Button variant="outlined" color="secondary" onClick={clearCart}  className="m-2" disabled={totalPrice === 0 || !isDelete}> Clear cart </Button>
          </div>
          </>
          : 
          <>
          {productToUpdate &&
            <Formik
            initialValues={initialValues}
            onSubmit={(values,{ resetForm }) => updateProduct(values, resetForm)}
            validationSchema={validationSchema}
              enableReinitialize={true}>
              {(props) => (
                <form style={{textAlign:'center'}} onSubmit={props.handleSubmit} autoComplete="off">
                  <div style={{display:'flex'}}>
                    <TextField className={classes.nameTextField} name="name" margin="normal" label="name" variant="outlined" fullWidth 
                    {...props.getFieldProps('name')} {...errorHelper(props,'name')}/>
                    <TextField name="lastname" margin="normal" label="Last name" variant="outlined" fullWidth {...props.getFieldProps('lastname')} {...errorHelper(props,'lastname')}/>     
                  </div>
              
                  <TextField name="username" margin="normal" label="User name" variant="outlined" fullWidth {...props.getFieldProps('username')} {...errorHelper(props,'username')}/>   
                          
                 
                                
                 
                                      
                

                  {/* Alert error */}
                  {/* <Collapse in={openAlert}>
                    <Alert
                      severity="error"
                      action={ <IconButton color="inherit" size="small" onClick={() => setOpenAlert(false) }> <CloseIcon fontSize="inherit" /> </IconButton>}>
                      {message}
                    </Alert>
                  </Collapse> */}

                  <Button className="my-3" variant="contained" color="primary" type="submit" size="large" fullWidth> Update product </Button>
                  <Grid container>
                    <Grid item>
                      <Link href="/signin" variant="body2"> Already have an account? Sign In </Link>
                    </Grid>
                  </Grid>    
                </form> )}
            </Formik>  
          }
          </>
        }
        
       
        
      
      </Drawer>
      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      >
        <div className={classes.drawerHeader} />
       
      </main>

    </div>
    
    
  );
}

export default SideBarCart
