import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { Button } from '@material-ui/core'
import Modal from 'react-bootstrap/Modal'
// React toastify 
import { ToastContainer, toast, Zoom } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'

const DeleteVacationModal = ({ modalOpen, setModalOpen, orderId }) => {
    const [buttonDisabled, setButtonDisabled] = useState(false)
    const dispatch = useDispatch()
    const products = useSelector(state => state.products)

    const successToast = (message) => {
        toast(message, { 
         draggable: true, 
         position: toast.POSITION.BOTTOM_RIGHT,
         transition: Zoom,
         autoClose: 2000
        })
      }


      const downloadTxtFile = () => {
        const fileArr = ['Your receipt:']
        const element = document.createElement("a")
        products.cartProducts.forEach(product => fileArr.push(`${product.amount} ${product.name} cost ${product.totalPrice}`))
        fileArr.push(`your order's total price is: ${products.totalPrice} `)
        const arr = [1,2,3,4,5]
        const file = new Blob([fileArr.join('\n')],    
                    {type: 'text/plain'})
        element.href = URL.createObjectURL(file);
        element.download = "myFile.txt";
        element.click();
      }
   
    
    
    return (
        <>
        <Modal size="md" centered show={modalOpen} onHide={() => setModalOpen(false)} style={{margin:'70px auto 30px', textAlign:'center'}}>
        <Modal.Header>
            <Modal.Title style={{fontWeight:'700'}}> Your order has been successfully placed! The order number is {orderId}</Modal.Title>
         </Modal.Header>
        <Modal.Body>
            <p>To download a receipt <Button onClick={downloadTxtFile}> click here </Button></p>
            <Button disabled={buttonDisabled} className="my-3" variant="contained" color="secondary" type="submit" size="large" onClick={() => setModalOpen(false)}> Delete vacation </Button>
        </Modal.Body>
        </Modal>
        {/* ToastContainer */}
        <ToastContainer /> 
        </>
    )
}

export default DeleteVacationModal
