export const setProductsState = (products) => ({ 
    type:'GET_PRODUCTS',
    payload:products
})

export const setCartProducts = (products) => ({ 
    type:'SET_CART_PRODUCTS',
    payload:products
})


export const addCartProducts = (product) => ({ 
    type:'ADD_CART_PRODUCTS',
    payload:product
})

export const updateTotalPrice = (price) => ({ 
    type:'UPDATE_TOTAL_PRICE',
    payload:price
})

export const resetTotalPrice = (price) => ({ 
    type:'RESET_TOTAL_PRICE',
    payload:price
})



const authUser = (user) => ({ 
    type:'AUTH_USER',
    payload:user
})

export const addVacationToRedux = (vacation) => ({ 
    type:'ADD_VACATION',
    payload:vacation
})

export const updateVacations = (vacations) => ({ 
    type:'UPDATE_VACATION',
    payload:vacations
})
