import React, { useEffect, useState } from 'react'
// Redux
import { useDispatch, useSelector } from 'react-redux'
import { setIsAdmin, setProductsState, setDrawerState } from '../../store/actions'
// Components
import NewUserModal from './newUserModal/NewUserModal'
import ProductCard from '../productCard/ProductCard'
// Material ui components
import { Grid, Grow } from '@material-ui/core'
import { makeStyles, StylesProvider } from '@material-ui/core/styles'
// React toastify 
import { ToastContainer } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
// Intro js
import { Steps } from 'intro.js-react'
import 'intro.js/introjs.css'
// Css
import './style.css'


// material ui style
const useStyles = makeStyles(() => ({
  root: {
    display:'flex',
    justifyContent:'center',
    margin:'30px'
  }
}))

 const Home = (props) => {
  const classes = useStyles()
  const user = JSON.parse(localStorage.getItem('user'))
  const cart = JSON.parse(localStorage.getItem('availableCart'))
  const dispatch = useDispatch()
  const products = useSelector(state => state.products.data)
  const cartProducts = useSelector(state => state.products.cartProducts)
  // Intro js states
  const [firstEntry, setFirstEntry] = useState(false)
  const [stepsEnabled, setStepsEnabled] = useState(false)
  const [initialStep, setInitialStep] = useState(0)
  const [steps, setSteps] = useState([
      { element:'#stepOne', intro: 'Here you can add new products to the cart ðŸ¥³', position: 'left', tooltipClass: 'myTooltipClass' },
      { element:'#stepTwo', intro: 'Increase the quantity in order to reach the desired quantityðŸ¥³', position: 'left', tooltipClass: 'myTooltipClass' },
      { element:'#stepThree', intro:'Here is your cart, threre you will find all the products you added ðŸ˜Ž', position: 'left', tooltipClass: 'myTooltipClass' },
      { element:'#stepFour', intro: 'Try to serach for your desired product ðŸ”', position: 'left', tooltipClass: 'myTooltipClass' },
      { element:'#stepFive', intro: 'navigate between the categories', position: 'left', tooltipClass: 'myTooltipClass' },
      { element:'#stepSix', intro: 'and when you finish click here to move to the payment page', position: 'left', tooltipClass: 'myTooltipClass' },
  ])


  // Get all the vacations
  const getProducts = async () => {
    try {

      const id = props.match.params.id
      const res = await fetch(`/product/${id}`)
      const { products } = await res.json()
      const response = await fetch(`/cart/${cart.id}`)
      const data  = await response.json()
      const productsArr = []
      products.forEach(product => {
        const productObj = {
          ...product,
        }
        const isInCart = data.cart.CartItems.find(item => item.ProductId === product.id)
        if(isInCart) {
          productObj.quantity = isInCart.amount
          productObj.isInCart = true
        } else {
          productObj.quantity = 1
        }
        productsArr.push(productObj)
      })
      dispatch(setProductsState(productsArr))
    } catch(err) {
      console.log(err)
    }
  }

  const reduceQuantity = (id) => {
    const productsArr = products
    const product = productsArr.find(product => product.id === id)
    product.quantity -= 1
    dispatch(setProductsState(productsArr))


  }

  const addQuantity = (id) => {
    const productsArr = products
    const product = productsArr.find(product => product.id === id)
    product.quantity += 1
    dispatch(setProductsState(productsArr))

  }
 
  // intro js functions
  const onExit = () => {
    setStepsEnabled(false)
    setFirstEntry(false)
  }

  // Trigger on component load
  useEffect(() => {
    getProducts()
  }, [props.match.params.id])

  useEffect(() => {
    getProducts()
   if (user.newUser) {
    setFirstEntry(true)
    dispatch(setDrawerState(true))
    // delete user['newUser']
    // localStorage.setItem('user', JSON.stringify(user))
   }
  }, [])

  useEffect(() => {
    user.admin ? dispatch(setIsAdmin(true)) : dispatch(setIsAdmin(false))
  }, [])

  return (
    <>
 
      {firstEntry ? 
      <>
      <NewUserModal stepsEnabled={stepsEnabled} setStepsEnabled={setStepsEnabled}/>
      <Steps enabled={stepsEnabled} steps={steps} initialStep={initialStep} onExit={onExit}/>
     </>
      : null}
      <StylesProvider injectFirst>
        <div className="container">
          <Grid container>
              {products ? 
                products.length !== 0 ?  products.map(product => (
                <Grow in={true}  timeout={700} key={product.id}>
                <Grid item xs={11} lg={5}  className={classes.root}  >
                  <ProductCard product={product} reduceQuantity={reduceQuantity} addQuantity={addQuantity} />
                </Grid> 
                </Grow>
              )): null :  null  }
            
          
           
         
            {/* ToastContainer */}
            <ToastContainer /> 
         
          </Grid>
        </div>
    </StylesProvider>
  </>
  )
}
export default Home
