import React from 'react'
// Material ui
import { Divider, IconButton, Card, CardContent, CardHeader,
CardMedia, Avatar, Typography } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'  
import DeleteIcon from '@material-ui/icons/Delete'
import EditIcon from '@material-ui/icons/Edit'
// Cloudinary
import { Image } from 'cloudinary-react'
import './style.css'

const useStyles = makeStyles(() => ({
    root: {
      display:'flex',
      justifyContent:'center',
      margin:'30px'
    },
    media: {
      height: '250px',
      width:'250px',
    },
    previewmedia: {
      width:'300px',
      height:'200px'
    },
    fab: {
      position:'fixed',
      bottom: '10px',
      left: '75%',
      whiteSpace: 'nowrap',
      zIndex: '50px',
      borderRadius:'20px',
      backgroundColor:'lightblue'
    },
    follow:{
      textAlign:'right'
    },
    card: {
      backgroundImage:'linear-gradient(#e6ecff, #99b3ff, #ffcccc)',
      "&:hover": {
          opacity:'0.8'
    }
  },
    title: { 
      fontWeight:'600',
      fontSize:'16px'
    },
    divider: {
      margin:'15px 0px'
    },
  }))     
  
const VacationCard = ({ product, editVacation, deleteVacation, setFollow, isAdmin }) => {
    const classes = useStyles()
    return (
      <>
     { product.image &&

      <div class="col">
              <div class="text-center bg-white border border-grey mb-1 product_container" >
                <img class="mb-2" src={product.image} id="bluecoach" height="250" width="347px" alt=""/>
                <div class="d-flex flex-wrap justify-content-between ">
                  <h6 class="text-danger text-left  m-1 ">Reduced:  </h6>
                  <button type="submit" class="text-white btn btn-primary btn-outline-dark add-cart mx-2 ">Add to Cart</button>
                </div>
    
                <p class="price text-left m-1"> Regular$ </p>
                <p class="text-dark text-left m-1">$</p>
              </div>
            </div>
        }
    
    </>
    )
}

export default VacationCard
