import React, { useEffect, useState } from 'react'
// Redux
import { useDispatch, useSelector } from 'react-redux'
import { setIsAdmin, setProductsState } from '../../store/actions'
// Components
import NewUserModal from './newUserModal/NewUserModal'
import EditModal from './editModal/EditModal'
import AddVacationModal from './addVacationModal/AddVacationModal'
import DeleteVacationModal from './deletevacation/DeleteVacationModal'
import ProductCard from '../productCard/ProductCard'
// Material ui components
import { Grid, Fab, Grow } from '@material-ui/core'
import { makeStyles, StylesProvider } from '@material-ui/core/styles'
// Material ui icons
import AddIcon  from '@material-ui/icons/Add'
// React toastify 
import { ToastContainer, toast, Zoom } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
// Intro js
import { Steps } from 'intro.js-react'
import 'intro.js/introjs.css'
// Css
import './style.css'


// material ui style
const useStyles = makeStyles((theme) => ({
  root: {
    display:'flex',
    justifyContent:'center',
    margin:'30px'
  },
  previewcard:{
    width:'300px',
    height:'340px',
    margin:'auto'
  },
  media: {
    height: '400px',
    width:'550px',
    paddingTop: '56.25%',
  },
  previewmedia: {
    width:'300px',
    height:'200px'
  },
  fab: {
    position:'fixed',
    bottom: '10px',
    left: '75%',
    whiteSpace: 'nowrap',
    zIndex: '50px',
    borderRadius:'20px',
    backgroundColor:'lightblue'
  },
  follow:{
    textAlign:'right'
  },
  card: {
    backgroundImage:'linear-gradient(#e6ecff, #99b3ff, #ffcccc)'
  },
  title: { 
    fontWeight:'600',
    fontSize:'16px'
  },
  divider: {
    margin:'15px 0px'
  },
}))

 const Home = (props) => {
  const classes = useStyles()
  const user = JSON.parse(localStorage.getItem('user'))
  const dispatch = useDispatch()
  const products = useSelector(state => state.products.data)
  const isAdmin = useSelector(state => state.users.isAdmin)
  // const [isAdmin, setIsAdmin] = useState(user ? user.admin : null)
  const [editModalOpen, setEditModalOpen] = useState(false)
  const [vacationToEdit, setVacationToEdit] = useState('')
  const [addVacationModalOpen, setAddVacationModalOpen] = useState(false)
  const [deleteVacationModalOpen, setDeleteVacationModalOpen] = useState(false)
  const [vacationToDelete, setVacationToDelete] = useState('')
  const [vacationToDeleteId, setVacationToDeleteId] = useState('')
  // Intro js states
  const [firstEntry, setFirstEntry] = useState(false)
  const [stepsEnabled, setStepsEnabled] = useState(false)
  const [initialStep, setInitialStep] = useState(0)
  const [steps, setSteps] = useState([
      { element:'#stepOne', intro: 'Take a look on the vacation we offer ü•≥', position: 'left', tooltipClass: 'myTooltipClass' },
      { element:'#stepTwo', intro: 'In each card you are able to see all the details about the vacation ü•≥', position: 'left', tooltipClass: 'myTooltipClass' },
      { element:'#stepThree', intro:'Start following vacations to get them on top of your page üòé', position: 'left', tooltipClass: 'myTooltipClass' },
      { element:'#stepFour', intro: 'Try to serach for your dreamy vacation, use the "search by" tab to take more specific search üîç', position: 'left', tooltipClass: 'myTooltipClass' },
  ])


  // Get all the vacations
  const getProducts = async () => {
    try {
      const id = props.match.params.id
      const res = await fetch(`/product/${id}`)
      const { products } = await res.json()
      const productsArr = []
      products.forEach(product => {
        const productObj = {
          ...product,
          quantity:1,
        }
        productsArr.push(productObj)
      })
      console.log(productsArr);
      dispatch(setProductsState(productsArr))
    } catch(err) {
      console.log(err)
    }
  }

  const reduceQuantity = (id) => {
    const productsArr = products
    const product = productsArr.find(product => product.id === id)
    product.quantity -= 1
    dispatch(setProductsState(productsArr))


  }

  const addQuantity = (id) => {
    const productsArr = products
    const product = productsArr.find(product => product.id === id)
    product.quantity += 1
    dispatch(setProductsState(productsArr))

  }


  // Edit vacation
  const editVacation = (id) => {
    setVacationToEdit(id)
    setEditModalOpen(true)
  }
 
  // intro js functions
  const onExit = () => {
    setStepsEnabled(false)
    setFirstEntry(false)
  }

  // Trigger on component load
  useEffect(() => {
    getProducts()
   if (user.newUser) {
    setFirstEntry(true)
    delete user['newUser']
    localStorage.setItem('user', JSON.stringify(user))
   }
  }, [props.match.params.id])

  useEffect(() => {
    user.admin ? dispatch(setIsAdmin(true)) : dispatch(setIsAdmin(false))
  }, [])

  return (
    <>
 
      {firstEntry ? 
      <>
      <NewUserModal stepsEnabled={stepsEnabled} setStepsEnabled={setStepsEnabled}/>
      <Steps enabled={stepsEnabled} steps={steps} initialStep={initialStep} onExit={onExit}/>
     </>
      : null}
      <StylesProvider injectFirst>
        <div className="container">
          <Grid container>
              {products ? 
                products.length !== 0 ?  products.map(product => (
                <Grow in={true}  timeout={700} key={product.id}>
                <Grid item xs={11} lg={5}  className={classes.root}  >
                  <ProductCard product={product} reduceQuantity={reduceQuantity} addQuantity={addQuantity} />
                </Grid> 
                </Grow>
              )): null :  null  }
            
          
           
         
            {/* ToastContainer */}
            <ToastContainer /> 
         
          </Grid>
        </div>
    </StylesProvider>
  </>
  )
}
export default Home
