import React, { useEffect } from 'react'
// Redux
import { useDispatch, useSelector } from 'react-redux'
import { setProductsState, addCartProducts } from '../../store/actions'
// Material ui
import { Divider, IconButton, Card, CardContent, CardHeader,
  Button, CardMedia, Avatar, Typography } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'  
import DeleteIcon from '@material-ui/icons/Delete'
import EditIcon from '@material-ui/icons/Edit'
import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
// Cloudinary
import { Image } from 'cloudinary-react'
import './style.css'

const useStyles = makeStyles(() => ({
    root: {
      display:'flex',
      justifyContent:'center',
      margin:'30px'
    },
    media: {
      height: '250px',
      width:'250px',
    },
    previewmedia: {
      width:'300px',
      height:'200px'
    },
    fab: {
      position:'fixed',
      bottom: '10px',
      left: '75%',
      whiteSpace: 'nowrap',
      zIndex: '50px',
      borderRadius:'20px',
      backgroundColor:'lightblue'
    },
    follow:{
      textAlign:'right'
    },
    card: {
      backgroundImage:"fff",
  },
    title: { 
      fontWeight:'600',
      fontSize:'16px'
    },
    divider: {
      margin:'15px 0px'
    },
  }))     
  
const VacationCard = ({ product, reduceQuantity, addQuantity }) => {
    const dispatch = useDispatch()
    const cart = JSON.parse(localStorage.getItem('availableCart'))
    const cartProducts = useSelector(state => state.products.cartProducts)
    const classes = useStyles()


    const addToCart = async (id, amount, price) => {
      const response = await fetch('/cart_item', { 
        method: 'POST',
        headers: {
         'Content-Type':'application/json'
       },
       body: JSON.stringify({ amount, price: price * amount, ProductId: id, CartId: cart.id })
       })
       const { cartItem } = await response.json() 
       console.log(cartItem);
       if(cartItem) {
        const cartItemsArr = cartProducts
        const changedItem = cartItemsArr.find(item => item.id === cartItem.ProductId)
        if(changedItem) {
          changedItem.amount = cartItem.amount
          changedItem.totalPrice = cartItem.price
         dispatch(setProductsState(cartItemsArr))
        } else {
          console.log('newwwwwww');
          // dispatch(addCartProducts(cartItemsArr))
        }
       }

    }
    useEffect(() => {
    console.log(product);
    }, [])
    return (
      <>
     { product.image &&

        <Card className={classes.card}>
   

          <CardMedia className={classes.media} image={product.image}></CardMedia>

          <CardContent id='stepTwo'>
            <Typography className={classes.likes}> 
              <span className="mini_title"> {product.name}</span>
            </Typography>
            <Divider className={classes.divider}/>
            <Typography className={classes.likes}> 
              <span className="mini_title"> price: </span>
              <span className="mini_content"> {product.price}$</span>
            </Typography>
            <Typography className={classes.likes}> 
              <span className="mini_title"> quantity </span>
              <span className="mini_content"> <RemoveIcon fontSize="small"  onClick={() => reduceQuantity(product.id)}/> {product.quantity} <AddIcon fontSize="small" onClick={() => addQuantity(product.id)}/> </span>
            </Typography>
            <Divider className={classes.divider}/>
        
          <Button onClick={() => addToCart(product.id, product.quantity, product.price )}>Add to cart</Button>
          </CardContent>
        </Card>  
        }
    
    </>
    )
}

export default VacationCard
