import React,{ useState } from 'react'
import { useHistory, Link } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { authUser } from '../../store/actions'
import SignInPhoto from '../../images/signPhoto.png'
import Carusel from '../caursel/Carusel'
// material ui
import { Hidden, Grid, TextField, Button, Paper, Collapse, IconButton, InputAdornment, ListItemAvatar,
Avatar, ListItem, List, ListItemText } from '@material-ui/core'
import CloseIcon from '@material-ui/icons/Close'
import VisibilityIcon from '@material-ui/icons/Visibility'
import VisibilityOffIcon from '@material-ui/icons/VisibilityOff'
import { makeStyles } from '@material-ui/core/styles';
import Alert from '@material-ui/lab/Alert';
// formik
import { useFormik } from 'formik'
import * as Yup from 'yup'
// css
import './style.css'
// cloudinary
import { Image } from 'cloudinary-react'
  
const useStyles = makeStyles((theme) => ({
    root: {
      marginTop:'20px',
    },
    image: {
      position:'relative',
      backgroundImage: `url(${SignInPhoto})`,
      backgroundRepeat: 'no-repeat',
      alignSelf: 'center',
      backgroundPosition: '0 0',
      backgroundSize: '454px 618px',
      flexBasis: '454px',
      height: '618px',
    },
    formGrid: {
      margin:'70px 15px '
    },
    paper: {
      margin: theme.spacing(8, 4),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    form: {
      width: '100%',
      marginTop: theme.spacing(1),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
}))

const SignIn = ({ setIsAuth }) => {
    const [message, setMessage] = useState('')
    const [openAlert, setOpenAlert] = useState(false)
    const [showPassword, setShowPassword] = useState(false)
    const [buttonDisabled, setButtonDisabled] = useState(false)
    const [dense, setDense] = useState(false);
    const [secondary, setSecondary] = useState(false);
    const history = useHistory()
    const dispatch = useDispatch()
    const classes = useStyles()

    // Formik
    const formik = useFormik({
      initialValues:{email:'',password:''},
      validationSchema:Yup.object({
          username:Yup.string()
          .required('sorry username is required'),
          password:Yup.string()
          .required('password is required')
          .min(6, 'password have to be at least 6 characters long')
      }),
      onSubmit:(values,{resetForm}) => {                               
        loginUser(values)
      }
    })

    const errorHelper = (formik,values) => ({
        error: formik.errors[values] && formik.touched[values] ? true : false,
        helperText: formik.errors[values] && formik.touched[values] ? formik.errors[values]:null
    })
  
    // Handle password visibility
    const  handleClickShowPassword = () => {
      setShowPassword(!showPassword)
    }
    function generate(element) {
      return [0, 1, 2].map((value) =>
        React.cloneElement(element, {
          key: value,
        }),
      );
    }
   
    // Log in user
    const loginUser = async (values) =>{
      try {
        setButtonDisabled(true)
        const response = await fetch('http://localhost:3001/user/login', { method: 'POST',
            headers: {
                'Content-Type':'application/json'
            },
            body: JSON.stringify(values)
        })
        const data = await response.json()
       if(data.success){
          localStorage.setItem('user',JSON.stringify(data.user))
          dispatch(authUser(data.user))
          setIsAuth(true)
          setButtonDisabled(false)
          history.push('/')
       } else {
          setMessage(data.error)
          setOpenAlert(true)
          setButtonDisabled(false)
       }  
    } catch(error) {
       console.log(error)
    }
  }

  return (
    <Grid container className={classes.root}>
   
        <Grid item xs={12} md={4} component={Paper}  square className={classes.formGrid}>
        <div className={classes.paper}>
        <img src="https://thumbs.dreamstime.com/b/vegetables-shopping-cart-trolley-grocery-logo-icon-design-vector-171090350.jpg" width="100" height="100" crop="scale" alt="cart" />
          <form className={classes.form} noValidate onSubmit={formik.handleSubmit}>
            <TextField  variant="outlined" margin="normal" fullWidth label="user name" name="email" autoFocus {...formik.getFieldProps('username')} {...errorHelper(formik,'username')}/>
            
            <TextField 
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={handleClickShowPassword}> {showPassword ? <VisibilityOffIcon /> : <VisibilityIcon />}  </IconButton>
                </InputAdornment>
                )}}  
                variant="outlined" margin="normal" fullWidth name="password" label="Password" type={showPassword ? "text": "password"} {...formik.getFieldProps('password')} {...errorHelper(formik,'password')}/>
                {/* Error Alert */}
                <Collapse in={openAlert}>
                  <Alert
                    severity="error"
                    action={ <IconButton color="inherit" size="small" onClick={() => setOpenAlert(false) }> <CloseIcon fontSize="inherit" /> </IconButton>}>
                    {message}
                  </Alert>
                </Collapse>

                <Button disabled={buttonDisabled} className="my-3" variant="contained" color="primary"type="submit" fullWidth variant="contained" color="primary" className={classes.submit} > Sign In </Button>
                
                <Grid container>
                  <Grid item>
                    <Link to="/signup" variant="body2"> Don't have an account? Sign Up </Link>
                  </Grid>
                </Grid>
            </form>
          </div>
        </Grid>
 

      {/* Sign in form */}
      <Grid item xs={12} md={4} component={Paper} square className={classes.formGrid}>
      <div className={classes.header}>
        <h1> Why us ?</h1>
      </div>
        
            <List dense={dense}>
              {generate(
                <ListItem>
                  <ListItemAvatar>
                    <Avatar>
                      <CloseIcon />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary="Single-line item"
                    secondary={secondary ? 'Secondary text' : null}
                  />
                </ListItem>,
              )}
            </List>
          
        </Grid>

        <Grid item xs={2}></Grid>
      </Grid>
    )
}
export default SignIn
